package stringclassmethods;

public class StringMethods {

	public static void main(String[] args) {
		
		// TODO Auto-generated method stub

		String a = "A";//  value of A = 65
		String b ="B";//  value of B = 66
		String c="A"; //String c ="A";
		int num='o';
		char d='Z';
		int value = d;
		char f='F';
		char O='g';
		int m=O;
		
		
		
		System.out.println("1  "+a.compareTo(b));
		System.out.println("2  "+ b.compareTo(a));
		System.out.println("3   "+a.charAt(0));
		System.out.println("4   "+a.equals(b));
		System.out.println("5   "+a.equals(c));
		System.out.println("6   "+d);
		System.out.println("7   "+num);
		System.out.println("8   "+(int)f);
		System.out.println("9   "+(int)d);
		System.out.println("10   "+value);
		System.out.println("11   "+m);
		System.out.println("12   "+O);
		System.out.println("12   "+(int)O);
		
		
		
		
		
	}

}
